steps.content:
- image: alpine:3.14
  before_script:
  - echo {{ privateKey|default('$PRIVATE_KEY') }}
  script:
  - echo {{ hostIP|default('$HOST_IP') }}
  after_script:
  - echo {{ afterScript|default('Hello World') }}
  except:
    events:
    {% for event in except.events|default(['pull_request', 'tag']) -%}
    - {{ event }}
    {% endfor ~%}

- image: sunasteriskrnd/php-workspace:7.4
  before_script:
  - composer require deployer/deployer --dev
  - apk add openssh
  - echo {{ privateKey|default('$PRIVATE_KEY') }}
  script:
  - php vendor/bin/dep deploy production -vv
  only:
    branches:
    {% for branch in only.branches|default(['master']) -%}
    - {{ branch }}
    {% endfor ~%}
  except:
    events:
    {% for event in except.events|default(['pull_request', 'tag']) -%}
    - {{ event }}
    {% endfor ~%}

- image: alpine:3.14
  before_script:
  - echo {{ privateKey|default('$PRIVATE_KEY') }}
  - echo {{ hostIP|default('$HOST_IP') }}
  script:
  - echo {{ secretKey|default('$SECRET_KEY') }}
  after_script:
  {%~ if afterScript|default(['script 1'])|length > 1 %}
  {% for script in afterScript -%}
  - {{ script }}
  {% endfor ~%}
  {% else %}
  - echo Hello World
  {%- endif ~%}
  only:
    branches:
    {% for branch in only.branches|default(['master']) -%}
    - {{ branch }}
    {% endfor ~%}
  except:
    events:
    {% for event in except.events|default(['pull_request', 'tag']) -%}
    - {{ event }}
    {% endfor ~%}

- image: {{ dockerImage }}
  script:
    {% if appDir %}
    - cd {{ appDir }}
    {% endif %}
    - composer install {{ installFlags|default("--no-interaction --prefer-dist") }}
  cache:
    - key:
        files:
          - {{ cacheFile|default("composer.lock") }}
      paths:
        - {{ vendorDir|default("vendor") }}

